import './signoff.css';
import { useState } from 'react';

function Signoff(props) {
  const [ signoffTime, setSignoff ] = useState(null);

  return (
    <button className="Signoff" onClick={ () => setSignoff(new Date()) }>
      {
        signoffTime ?
          <span className="signoff-name">Mayank Jain</span>
          : <span className="signoff-name">Mayank Jain?</span>
      }
      {
        signoffTime
          ? <div className="signoff-time">{}</div>
          : <div className="signoff-time">`Last viewed: {signoffTime.toString("dddd, MMMM Do YYYY, h:mm:ss")}`</div>
      }
    </button>
  );
}

export default Signoff;
